    spending_credit_card_by_month[m] = (spending_credit_card_by_month[m] || 0) + 1;
  }
  for (const m of Object.keys(spending_credit_card_by_month)) {
    month_with_more_credit_card_transactions = (spending_credit_card_by_month[m] >= (spending_credit_card_by_month[month_with_more_credit_card_transactions] || 0)) ? m : month_with_more_credit_card_transactions;
