  categories_01m = _.groupBy(spending.filter(t => t.date.getTime() >= oneMonthsAgo && t.date.getTime() <= this_moment), 'olivia_category_level1_id');
  categories_03m = _.groupBy(spending.filter(t => t.date.getTime() >= threeMonthsAgo && t.date.getTime() <= this_moment), 'olivia_category_level1_id');
  categories_12m = _.groupBy(spending.filter(t => t.date.getTime() <= end_year), 'olivia_category_level1_id');
  categories_this_month = _.groupBy(spending.filter(t => t.date.getTime() >= start_this_month && t.date.getTime() <= this_moment), 'olivia_category_level1_id');
  variable_categories_this_month = _.groupBy(spending.filter(t => t.date.getTime() >= start_this_month && t.date.getTime() <= this_moment && t.budget_type === 'variable'), 'olivia_category_level1_id');
  transactions_03m = _.size(spending.filter(t => t.date.getTime() >= threeMonthsAgo && t.date.getTime() <= this_moment));
  food_and_drink_03m = -_.sumBy(spending.filter(t => t.date.getTime() >= threeMonthsAgo && t.date.getTime() <= this_moment && [60000].includes(t.olivia_category_level1_id)), 'amount').toFixed(2);
  groceries_03m = -_.sumBy(spending.filter(t => t.date.getTime() >= threeMonthsAgo && t.date.getTime() <= this_moment && [115000].includes(t.olivia_category_level1_id)), 'amount').toFixed(2);
  spending_01m = -_.sumBy(spending.filter(t => t.date.getTime() >= oneMonthsAgo && t.date.getTime() <= this_moment), 'amount').toFixed(2);
  spending_this_month = -_.sumBy(spending.filter(t => t.date.getTime() >= start_this_month && t.date.getTime() <= this_moment), 'amount').toFixed(2);
  spending_03m_closed = -_.sumBy(spending.filter(t => t.date.getTime() >= start_3mo_closed && t.date.getTime() <= end_3mo_closed), 'amount').toFixed(2);
  fixed_spending_01m = -_.sumBy(spending.filter(t => t.date.getTime() >= oneMonthsAgo && t.date.getTime() <= this_moment && t.budget_type === 'fixed'), 'amount').toFixed(2);
  variable_spending_01m = -_.sumBy(spending.filter(t => t.date.getTime() >= oneMonthsAgo && t.date.getTime() <= this_moment && t.budget_type === 'variable'), 'amount').toFixed(2);
