  variable_spending_this_month = -_.sumBy(spending.filter(t => t.date.getTime() >= start_this_month && t.date.getTime() <= this_moment && t.budget_type === 'variable'), 'amount').toFixed(2);
  fixed_spending_03m_closed = -_.sumBy(spending.filter(t => t.date.getTime() >= start_3mo_closed && t.date.getTime() <= end_3mo_closed && t.budget_type === 'fixed'), 'amount').toFixed(2);
  variable_spending_03m_closed = -_.sumBy(spending.filter(t => t.date.getTime() >= start_3mo_closed && t.date.getTime() <= end_3mo_closed && t.budget_type === 'variable'), 'amount').toFixed(2);
  fixed_spending_06m_closed = -_.sumBy(spending.filter(t => t.date.getTime() >= start_6mo_closed && t.date.getTime() <= end_6mo_closed && t.budget_type === 'fixed'), 'amount').toFixed(2);
  variable_spending_06m_closed = -_.sumBy(spending.filter(t => t.date.getTime() >= start_6mo_closed && t.date.getTime() <= end_6mo_closed && t.budget_type === 'variable'), 'amount').toFixed(2);
