    const m = moment(income_list_12m[i].date).format('MM');
    income_by_month[m] = (income_by_month[m] || 0) + income_list_12m[i].amount;
  }
  for (const m of Object.keys(income_by_month)) {
    month_with_more_deposits = (income_by_month[m] >= (income_by_month[month_with_more_deposits] || 0)) ? m : month_with_more_deposits;
  }
  income_12m = _.sumBy(income_list_12m, 'amount').toFixed(2);
  income_06m = _.sumBy(income.filter(t => t.date.getTime() >= sixMonthsAgo && t.date.getTime() <= this_moment), 'amount').toFixed(2);
  income_03m = _.sumBy(income.filter(t => t.date.getTime() >= threeMonthsAgo && t.date.getTime() <= this_moment), 'amount').toFixed(2);
  income_01m = _.sumBy(income.filter(t => t.date.getTime() >= oneMonthsAgo && t.date.getTime() <= this_moment), 'amount').toFixed(2);
  income_03m_closed = _.sumBy(income.filter(t => t.date.getTime() >= start_3mo_closed && t.date.getTime() <= end_3mo_closed), 'amount').toFixed(2);
  income_this_month = _.sumBy(income.filter(t => t.date.getTime() >= start_this_month && t.date.getTime() <= this_moment), 'amount').toFixed(2);
  income_paycheck_this_month = _.sumBy(income.filter(t => t.date.getTime() >= start_this_month && t.date.getTime() <= this_moment && [150300].includes(t.olivia_category_id)), 'amount').toFixed(2);
}

let annual_savings_year = income_12m - spending_12m;
let annual_savings_2020 = income_12m - spending_12m;

return {
  v: '3.3',
  update_at: moment().format('llll'),
  spending_06m,
  spending_03m,
  spending_transactions_12m,
