  fixed_spending_this_month,
  variable_spending_this_month,
  income_12m,
  income_06m,
  income_03m,
  income_01m,
  income_03m_closed,
  income_this_month,
  income_paycheck_this_month,
  income_by_month,
  most_expensive_transaction_01m_closed,
  most_expensive_transaction_03m,
  month_with_more_deposits,
