  max_credit_card_transactions,
  top_03_spending_by_category_weekdays_12m,
  top_03_spending_by_category_weekend_12m,
  savings_unpaired_transactions_03m,
  savings_investments_accounts_03m,
  savings_03m,
  date_last_spending_transaction,
  date_last_income_transaction
}

if(!rvars.agg
  || !rvars.agg.total_spending_and_income
  || !rvars.agg.total_spending_and_income.totals
  || (!rvars.agg.total_spending_and_income.totals.income_03m && parseFloat(rvars.agg.total_spending_and_income.totals.income_03m) !== 0)
  || (!rvars.agg.total_spending_and_income.totals.spending_03m && rvars.agg.total_spending_and_income.totals.spending_03m !== 0)
) return false;

if(rvars.agg.total_spending_and_income.totals.income_03m < rvars.agg.total_spending_and_income.totals.spending_03m) return true;

return false;

