const moment = require('moment');

const unusualExpenseVariation = 15;
const transactionMinAmt = 30.00;

const start_last_month = moment.utc().subtract(1, 'month').startOf('month').valueOf();
const end_last_month = moment.utc().subtract(1, 'month').endOf('month').valueOf();
const start_actual_month = moment.utc().startOf('month').valueOf();
const end_actual_month = moment.utc().endOf('month').valueOf();

const agg = [];
const billsRelDesc = {};

for (const transaction of transactions) {
  const m = transaction.date.getTime();
  if (transaction.cashflow
    && transaction.enabled
    && !transaction.pending
    && m >= start_last_month
    && m <= end_actual_month
    && [10100, 10200, 10300, 10400, 10500, 10600].includes(transaction.olivia_category_id)
  ) {
    // group bills per relevant description and category, summing previous and current month separately
    const relDesc = `${transaction.olivia_category_id}${transaction.relevant_description}`;
    billsRelDesc[relDesc] = billsRelDesc[relDesc]
      || {
      previous: 0,
      current: 0,
      transaction_id: transaction.transaction_id,
      description: transaction.description,
      vendor: transaction.relevant_description,
      category_id: transaction.olivia_category_id,
    };
    if (m <= end_last_month) {
      billsRelDesc[relDesc].previous -= transaction.amount;
    } else if (m >= start_actual_month) {
      billsRelDesc[relDesc].current -= transaction.amount;
    }
  }
}

// find out which bills should be talked about
for (const relDesc in billsRelDesc) {
  // previous month expense must be higher than a minimum amount
