// };

// usando etl que filtra cartÃµes
const AGGREGATION = {
    115000: rvars.agg.l1cat115000.sum15days4monthswithoutcreditcard,
    60000: rvars.agg.l1cat60000.sum15days4monthswithoutcreditcard,
    1000014: rvars.agg.l1cat1000014.sum15days4monthswithoutcreditcard,
    70300: rvars.agg.l2cat70300.sum15days4monthswithoutcreditcard
};

const MONTHS = [
    'current_month',
    '1_month_ago',
    '2_month_ago',
    '3_month_ago'
]

const tally = {};

for (const category of CATEGORIES) {
    tally[category] = {};
    tally[category]['enable'] = true;
    for (const month of MONTHS) {
        if (!(AGGREGATION[category][month] > 0)) tally[category]['enable'] = false;
    }
}

// Check if there is at least one category that attends the higher expenses criteria
const enabled_categories = [];
for (const category of CATEGORIES) {
    if (tally[category]['enable']) {
        const period_amounts = MONTHS.map((m) => AGGREGATION[category][m]);
        const past_expenses = period_amounts.slice(1);
        const avg_amount = past_expenses.reduce((acc, item) => acc + item) / 3;

        // Applicable if current amount between 97% and 103% of average
        let start = avg_amount * 0.97;
        let end = avg_amount * 1.03;
        if (period_amounts[0] >= start && period_amounts[0] <= end) {
            enabled_categories.push(category);
        }
    }
}

if (enabled_categories.length == 0) {
    return {};
}

// Randomly selecting the conversation category:
// context.localMoment should be fixed while generating both session and push notification
// so this should be a 'fixed' way of making it random in the sense of generating pushes
// as well as synching with svars
const random_seed = moment(context.localMoment).format('x') % enabled_categories.length;
const random_category = enabled_categories[random_seed];

const period_amounts = MONTHS.map((m) => AGGREGATION[random_category][m]);
const period_months = [0, 1, 2, 3].map((months_ago) => moment().startOf('month').subtract(months_ago, 'month').locale('pt-BR').format('MMMM'));

// Exclude this month's expenses
const past_expenses = period_amounts.slice(1);
const avg_amount = past_expenses.reduce((acc, item) => acc + item) / 3;

const diff_amount = Math.abs(period_amounts[0] - avg_amount);

const category_name = category(random_category).full_name;

return {
    period_amounts,
    period_months,
    diff_amount,
    category_name
};

