    r => !r.hidden && r.period === 'month' && r.frequency <= 1 && r.category === 150300
);

return regularPaychecks.length ? 1 : 0;

const moment = require('moment');
if (!rvars || !rvars.aggregate_missing_bills || !rvars.aggregate_missing_bills.length) {
  return 0;
}

const { missing_bills_history = {} } = ivars || {};
for (const bill of rvars.aggregate_missing_bills) {
  const { hash, description, customized_description, avg_day_of_month, avg_amount } = bill;
  if (hash == null || description == null || avg_day_of_month == null || avg_amount == null) continue;
  let lastTime = 0;
  for (const key in missing_bills_history) {
    const [keyHash, keyDesc, keyCustomDesc] = key.split('|');
    if (
      hash == parseInt(keyHash) ||
      (description.length && description == keyDesc) ||
      (keyCustomDesc.length && customized_description == keyCustomDesc)
    ) {
      lastTime = Math.max(missing_bills_history[key], lastTime);
    }
  }
  let month_to_check = moment().month();

  if(moment().date() <= avg_day_of_month) {
    month_to_check = (month_to_check + 11) % 12;
  }

  if(moment().diff(moment(lastTime), 'month') > 0 && moment().isBefore(moment([moment().year(), month_to_check, avg_day_of_month]).add(7,'days'))) {
    return 1;
  }
}

return 0;

