const moment = require('moment');

const LOAN_OR_INVESTMENT_ACCOUNT_TYPE_IDS = [103, 104, 105, 201, 202, 203];
const seven_days_ago = moment.utc().subtract(7, 'days').startOf('day').valueOf();
const current_time = moment.utc().valueOf();
const income_array = [];

for (const transaction of transactions) {
    const m = transaction.date.getTime();
    if (transaction.cashflow
        && transaction.cashflow_type === 'in'
        && !LOAN_OR_INVESTMENT_ACCOUNT_TYPE_IDS.includes(transaction.account_type_id)
        && transaction.enabled
        && !transaction.pending
        && m >= seven_days_ago
        && m <= current_time
    ) {
        const transaction_data = {
            amount: transaction.amount,
            timestamp: transaction.date.getTime(),
            formatted_date: moment(transaction.date.getTime()).format('DD/MM/YY'),
            transaction_id: transaction.transaction_id,
            olivia_category_id: transaction.olivia_category_id,
            description: transaction.description
        };

        income_array.push(transaction_data);
    }
}

income_array.sort((a, b) => a.timestamp - b.timestamp);

return income_array;

const moment = require('moment');

if (!rvars
  || !rvars.agg
  || !rvars.agg.income_transactions
  || !rvars.agg.income_transactions.last_7d) return 0;

for (const transaction of rvars.agg.income_transactions.last_7d) {
  if (moment().endOf('day').diff(moment(transaction.timestamp).startOf('day'), 'days') > 3
    || !(transaction.olivia_category_id == 150300)
    || (transaction.amount < 100)
  ) continue;

  if (!!ivars.you_got_paid_paycheck_historic
    && ivars.you_got_paid_paycheck_historic.some((hist) => hist == transaction.transaction_id)) continue;

  return 1;
}

return 0;

if (!dat
  || !dat.profile_overview_panel_section
  || !dat.profile_overview_panel_section.totals.income
  || !dat.profile_overview_panel_section.totals.spending
) return false;

const _ = require('lodash');
const moment = require('moment');

const income_data = _.get(dat.profile_overview_panel_section, 'totals.income');
const spending_data = _.get(dat.profile_overview_panel_section, 'totals.spending');

// Get closed months only
const months = [
  moment().startOf('month').subtract(3, 'month').format('YYYY-MM-DD'),
  moment().startOf('month').subtract(2, 'month').format('YYYY-MM-DD'),
  moment().startOf('month').subtract(1, 'month').format('YYYY-MM-DD')
];

const monthly_income = months.map((date) => {
  const object = _.find(income_data, { 'begin': date });
  if (object) return Math.abs(object.amount.total);
  return 0;
}).reduce((acc, value) => acc + value);

const monthly_spending = months.map((date) => {
  const object = _.find(spending_data, { 'begin': date });
  if (object) return Math.abs(object.amount.total);
  return 0;
}).reduce((acc, value) => acc + value);

return monthly_spending <= monthly_income;

