const LOAN_OR_INVESTMENT_ACCOUNT_TYPE_IDS = [103, 104, 105, 201, 202, 203];
const seven_days_ago = moment.utc().subtract(7, 'days').startOf('day').valueOf();
const current_time = moment.utc().valueOf();
const income_array = [];

for (const transaction of transactions) {
    const m = transaction.date.getTime();
    if (transaction.cashflow
        && transaction.cashflow_type === 'in'
        && !LOAN_OR_INVESTMENT_ACCOUNT_TYPE_IDS.includes(transaction.account_type_id)
        && transaction.enabled
        && !transaction.pending
        && m >= seven_days_ago
        && m <= current_time
    ) {
        const transaction_data = {
            amount: transaction.amount,
            timestamp: transaction.date.getTime(),
            formatted_date: moment(transaction.date.getTime()).format('DD/MM/YY'),
            transaction_id: transaction.transaction_id,
            olivia_category_id: transaction.olivia_category_id,
            description: transaction.description
        };

        income_array.push(transaction_data);
    }
}

income_array.sort((a, b) => a.timestamp - b.timestamp);

return income_array;

