const moment = require('moment');
const _ = require('lodash');

const start_one_week_ago = moment.utc().subtract(1, 'week').valueOf();
const end_current_week = moment.utc().endOf('week').valueOf();
const billsRelDesc = {};

for (const transaction of transactions) {
    const m = transaction.date.getTime();
    if (transaction.cashflow
        && transaction.enabled
        && !transaction.pending
        && m >= start_one_week_ago
        && m <= end_current_week
        && transaction.cashflow === true
        && transaction.cashflow_type === 'out'
        && !([260000].includes(transaction.olivia_category_level1_id))
    ) {
        // group bills per relevant description and category, summing previous and current month separately
        const relDesc = `${transaction.olivia_category_id}_${transaction.relevant_description}_${transaction.amount}_${transaction.account_type_id}`;
        billsRelDesc[relDesc] = billsRelDesc[relDesc] || [];
        const transaction_data = {
            amount: -transaction.amount,
            timestamp: transaction.date.getTime(),
            formatted_date: moment(transaction.date.getTime()).format('DD/MM/YY'),
            transaction_id: transaction.transaction_id,
            description: transaction.description,
            account_type_id: transaction.account_type_id,
            metadata: transaction.metadata
        };

        billsRelDesc[relDesc].push(transaction_data);
    }
}

const duplicateEntries = _.pickBy(billsRelDesc, function (value, key) {
    if (value.length < 2) return false;
    return true;
});

return duplicateEntries;

if (!rvars
    || !rvars.agg
    || !rvars.agg.duplicate_transactions
    || !rvars.agg.duplicate_transactions.last_7d) return 0;

const duplicates = rvars.agg.duplicate_transactions.last_7d;
const moment = require('moment');

//Initialize history if not exists
ivars.duplicated_payment_history = ivars.duplicated_payment_history || [];

for (let key in duplicates) {
    const sorted_entries = duplicates[key].sort((a, b) => { return moment(a.timestamp).format('x') - moment(b.timestamp).format('x') });
    for (var entry = 0; entry < sorted_entries.length; entry++) {
        //Skip if not checking account
        if (sorted_entries[entry].account_type_id != 100) continue;
        //Check for history
        if (ivars.duplicated_payment_history.includes(sorted_entries[entry].transaction_id)) continue;
        //If there is no next element
        if (!sorted_entries[entry + 1]) continue;
        //Check if entries are on the same day
        if (moment(sorted_entries[entry].timestamp).add(1, 'day').isAfter(moment(sorted_entries[entry + 1].timestamp))) return 1;
    }
}

return 0;

const moment = require('moment');

const start_six_months_ago = moment.utc().subtract(6, 'months').startOf('month').valueOf();
const end_current_month = moment.utc().endOf('month').valueOf();
const billsRelDesc = {};

for (const transaction of transactions) {
    const m = transaction.date.getTime();
    if (transaction.cashflow
        && transaction.enabled
        && !transaction.pending
        && m >= start_six_months_ago
        && m <= end_current_month
        && [10100, 10200, 10300, 10400, 10500, 10600].includes(transaction.olivia_category_id)
    ) {
        // group bills per relevant description and category, summing previous and current month separately
        const relDesc = `${transaction.olivia_category_id}${transaction.relevant_description}`;
        billsRelDesc[relDesc] = billsRelDesc[relDesc] || [];
        const transaction_data = {
            amount: -transaction.amount,
            account_id: transaction.account_id,
            timestamp: transaction.date.getTime(),
            formatted_date: moment(transaction.date.getTime()).format('DD/MM/YY'),
            month: moment(transaction.date.getTime()).month(),
            transaction_id: transaction.transaction_id,
            description: transaction.description
        };

        billsRelDesc[relDesc].push(transaction_data);
    }
}

for (const relDesc in billsRelDesc) {
    billsRelDesc[relDesc].sort((a, b) => a.timestamp - b.timestamp);
    let last_value = 0;
    let last_month = -1;
    for (const i in billsRelDesc[relDesc]) {
        const previous_month_number = (billsRelDesc[relDesc][i].month + 11) % 12;
        if (last_month >= 0 && last_month == previous_month_number && last_value != 0) {
            billsRelDesc[relDesc][i].variation = (billsRelDesc[relDesc][i].amount / last_value) - 1;
        }
        else {
            billsRelDesc[relDesc][i].variation = 0;
        }
        last_value = billsRelDesc[relDesc][i].amount;
        last_month = billsRelDesc[relDesc][i].month;
    };
}

return billsRelDesc;

const moment = require('moment');

if (!rvars
  || !rvars.agg
  || !rvars.agg.bills_by_category_and_description
  || !rvars.agg.bills_by_category_and_description.last_6m) return 0;

const vendors = [
  'sabesp '
];

for (const vendor in rvars.agg.bills_by_category_and_description.last_6m) {
  if (vendors.some(v => vendor.includes(v))) {
    const transactions = rvars.agg.bills_by_category_and_description.last_6m[vendor];

    if (transactions.length < 3) continue;

    // Check if there are 2 transactions or more on the same month
    let memory_object = {};
    if (transactions.some((transaction) => {
      if (!memory_object[transaction.month]) {
        memory_object[transaction.month] = true;
        return false;
      }
      else {
        return true;
      }
    })) {
      continue;
    }

    const last_transaction = transactions[transactions.length - 1];
    const before_last_transaction = transactions[transactions.length - 2];
    if (moment().endOf('day').diff(moment(last_transaction.timestamp).startOf('day'), 'days') > 7
      || (!!ivars.unusual_expenses_agua_historic && ivars.unusual_expenses_agua_historic.some((hist) => hist == last_transaction.transaction_id))) continue;

    const rounded_variation = parseFloat((Math.round(parseFloat(`${last_transaction.variation}e+2`)) + 'e-2'));

    if (!(before_last_transaction.amount >= 30 && before_last_transaction.amount <= 60 && rounded_variation >= 0.3)
      && !(before_last_transaction.amount > 60 && before_last_transaction.amount <= 100 && rounded_variation >= 0.25)
      && !(before_last_transaction.amount > 100 && before_last_transaction.amount <= 150 && rounded_variation >= 0.2)
      && !(before_last_transaction.amount > 150 && rounded_variation >= 0.2)) continue;

    return 1;
  }
}

return 0;

