const _ = require('lodash');
const moment = require('moment');
const { category, currency } = formulas;

const spending_data = _.get(dat.profile_overview_panel_section, 'totals.spending');

// Get closed months only
const months = [
    moment().startOf('month').subtract(3, 'month').format('YYYY-MM-DD'),
    moment().startOf('month').subtract(2, 'month').format('YYYY-MM-DD'),
    moment().startOf('month').subtract(1, 'month').format('YYYY-MM-DD')
];

const monthly_category_spending = months.map((date) => {
    const object = _.find(spending_data, { 'begin': date });
    if (object) return object.by_category;
    return undefined;
}).filter(n => n);

// List of spending sum by category
const category_spending_sum = {}

monthly_category_spending.forEach((monthly_spending) => {
    const categories = Object.keys(monthly_spending);
    categories.forEach((cat) => category_spending_sum[cat] = (category_spending_sum[cat] || 0) + ((monthly_spending[cat] || { total: 0 }).total));
});

// Top 3 categories by spending
const category_ranking = Object.keys(category_spending_sum).sort((a, b) => category_spending_sum[a] - category_spending_sum[b]).slice(0, 3);

const category_summary = category_ranking.map((top_spending) => `<b>${category(top_spending).full_name} ${currency(Math.abs(category_spending_sum[top_spending]))}</b>`).join('<br/>');

const monthly_summary = category_ranking.map((top_spending) => {
    const summary = []
    for (let i = 0; i < monthly_category_spending.length; i++) {
        let total = Math.abs((monthly_category_spending[i][top_spending] || { total: 0 }).total);
        if (total)
            summary.push(`<b>â¡</b> ${moment(months[i]).locale("PT-BR").format("MMMM")} <b>${currency(total)}</b>`)
    }
    return `<b>${category(top_spending).full_name}</b><br/>${summary.join('<br/>')}`;
}).join('<br/>');

const highest_expense = `<b>${rvars.agg.general_transactions.highest_expense_3_closed_months.desc} ${currency(Math.abs(rvars.agg.general_transactions.highest_expense_3_closed_months.amt))}</b>`;

return { category_summary, monthly_summary, highest_expense };

