const moment = require('moment');

const start = moment().subtract(3, 'months').startOf('month').valueOf();
const end = moment().subtract(1, 'months').endOf('month').valueOf();

transactions = transactions.filter(t => t.date.getTime() >= start
                                        && t.date.getTime() <= end
                                        && (t.budget_type === 'variable' || t.budget_type === 'fixed')
                                        && t.cashflow === true
                                        && t.cashflow_type === 'out'
                                        && t.amount < 0
                                        && t.enabled);

if (transactions.length < 1) return null;
