const moment = require('moment');

const start_six_months_ago = moment.utc().subtract(6, 'months').startOf('month').valueOf();
const end_current_month = moment.utc().endOf('month').valueOf();
const billsRelDesc = {};

for (const transaction of transactions) {
    const m = transaction.date.getTime();
    if (transaction.cashflow
        && transaction.enabled
        && !transaction.pending
        && m >= start_six_months_ago
        && m <= end_current_month
        && [10100, 10200, 10300, 10400, 10500, 10600].includes(transaction.olivia_category_id)
    ) {
        // group bills per relevant description and category, summing previous and current month separately
        const relDesc = `${transaction.olivia_category_id}${transaction.relevant_description}`;
        billsRelDesc[relDesc] = billsRelDesc[relDesc] || [];
        const transaction_data = {
            amount: -transaction.amount,
            account_id: transaction.account_id,
            timestamp: transaction.date.getTime(),
            formatted_date: moment(transaction.date.getTime()).format('DD/MM/YY'),
            month: moment(transaction.date.getTime()).month(),
            transaction_id: transaction.transaction_id,
            description: transaction.description
        };

        billsRelDesc[relDesc].push(transaction_data);
    }
}

for (const relDesc in billsRelDesc) {
    billsRelDesc[relDesc].sort((a, b) => a.timestamp - b.timestamp);
    let last_value = 0;
    let last_month = -1;
    for (const i in billsRelDesc[relDesc]) {
        const previous_month_number = (billsRelDesc[relDesc][i].month + 11) % 12;
        if (last_month >= 0 && last_month == previous_month_number && last_value != 0) {
            billsRelDesc[relDesc][i].variation = (billsRelDesc[relDesc][i].amount / last_value) - 1;
        }
        else {
            billsRelDesc[relDesc][i].variation = 0;
        }
        last_value = billsRelDesc[relDesc][i].amount;
        last_month = billsRelDesc[relDesc][i].month;
    };
}

return billsRelDesc;

